Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_TEAMS_ETC_ACT_CHECK_SP" 
	AS
	BEGIN
		LOOP
			UPDATE PRTEAM
			   SET ODF_SS_ASSGNS_CURVE = 1
			WHERE PRID IN
			(
              SELECT t.prID
				FROM PRTeam t, PRASSIGNMENT a
			   WHERE t.prid = a.team_id
                 and a.slice_status = 1
                 and t.odf_ss_assgns_curve is null
			)
            AND ROWNUM <= 10000;

			EXIT WHEN SQL%ROWCOUNT = 0;
			COMMIT;
		END LOOP;
	END;
