Record.definition:
CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_CLIMB_ROLES_SP" (p_roleID NUMBER, p_parentID NUMBER) IS
v_currentID NUMBER;
CURSOR parentRole IS SELECT prID, prParentID FROM PRJ_RESOURCES WHERE prIsRole <> 0 AND prID = p_parentID ORDER BY prID ;
BEGIN FOR eachRecord IN parentRole LOOP
CMN_ID_SP('PRJ_ROLES_FLAT', v_currentID);
INSERT INTO PRJ_ROLES_FLAT ( ID, ROLE_ID, BRANCH_ROLE_ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY ) VALUES ( v_currentID, p_roleID, eachRecord.prID, sysdate, 1, sysdate, 1 );
IF eachRecord.prParentID IS NOT NULL THEN PRJ_CLIMB_ROLES_SP(p_roleID, eachRecord.prParentID); END IF; END LOOP; END PRJ_CLIMB_ROLES_SP;
