Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_FISCAL_BLB_SLICE_DELETE_SP" 
	(
		P_PRJ_REQUEST_ID        NUMBER,
		P_DROPOFF_DATE		      DATE DEFAULT NULL,
		P_TABLE_NAME			      VARCHAR2 DEFAULT 'PRJ_FISCAL_BLB_SLICES',
		P_NEXT_FROM_DATE	      DATE DEFAULT NULL,
		P_ENTITY_ID             NUMBER,
	  P_PERIOD_TYPE           VARCHAR2 DEFAULT 'MONTHLY'
	)
	AS
	V_SQL                       VARCHAR2 (1000);
	BEGIN
		IF P_DROPOFF_DATE IS NULL THEN
			V_SQL := 'DELETE ' || P_TABLE_NAME || ' WHERE SLICE_REQUEST_ID = ' || P_PRJ_REQUEST_ID;
	  ELSE
      V_SQL := 'DELETE ' || P_TABLE_NAME
            || ' WHERE SLICE_REQUEST_ID = ' || P_PRJ_REQUEST_ID
            || '   AND PERIOD_ID IN ( SELECT ID FROM BIZ_COM_PERIODS WHERE ENTITY_ID = ' || P_ENTITY_ID
            || '   AND PERIOD_TYPE = ''' || P_PERIOD_TYPE || ''''
            || '   AND ( START_DATE < ''' || P_DROPOFF_DATE || ''' OR START_DATE > ''' || P_NEXT_FROM_DATE || '''))';
    END IF;

    V_SQL := V_SQL || ' AND ROWNUM <= 10000';

    LOOP
        EXECUTE IMMEDIATE V_SQL;
        EXIT WHEN SQL%ROWCOUNT = 0;
        COMMIT;
    END LOOP;

	END;
