Record.definition:
CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_PROCESS_OBS_UNITS_SP" IS v_currentID NUMBER;
CURSOR allUnits IS SELECT PRJ_OBS_UNITS.ID, PRJ_OBS_UNITS.PARENT_ID FROM PRJ_OBS_UNITS ;
BEGIN DELETE FROM PRJ_OBS_UNITS_FLAT;
FOR eachRecord IN allUnits LOOP
CMN_ID_SP('PRJ_OBS_UNITS_FLAT', v_currentID);
INSERT INTO PRJ_OBS_UNITS_FLAT ( ID, UNIT_ID, BRANCH_UNIT_ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY ) VALUES ( v_currentID, eachRecord.ID, eachRecord.ID, sysdate, 1, sysdate, 1 );
IF eachRecord.PARENT_ID IS NOT NULL THEN prj_climb_units_sp(eachRecord.ID, eachRecord.PARENT_ID); END IF;
END LOOP; END PRJ_PROCESS_OBS_UNITS_SP;
