Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."REV_PRC_GETWIPADJAPPROVERECD" (
adjustgroupno 	NUMBER ,
reverse_ 	NUMBER  DEFAULT NULL,
temp_rc1 IN	OUT REV_PRC_GETWIPADJAPPRECDTLPkg.RCT1)
AS
StoO_selcnt	INTEGER;
StoO_error 	INTEGER;
StoO_rowcnt	INTEGER;
StoO_crowcnt	INTEGER := 0;
StoO_fetchstatus	INTEGER := 0;
StoO_errmsg	GLOBALPKG.STRING;
StoO_sqlstatus	INTEGER;
/* temp_RC1 	REV_PRC_GETWIPADJAPPRECDTLPkg.RCT1; - MOVED this as a parameter as it
 needs to be returned as a result set. */
BEGIN
/*
   Called From	 	 :
   Procedure               :-
   Trigger                    :-
   PMD TransactionId  :-
   WMD ActionId         :-
   Modified By             :  sundar
   Modified Date          :  07/25/2001
*/

	IF  NVL(REV_PRC_GETWIPADJAPPROVERECD.reverse_, 1) = 1 THEN
		BEGIN
		REV_PRC_GETWIPADJAPPRECDTL(adjustgroupno=>REV_PRC_GETWIPADJAPPROVERECD.adjustgroupno,
		 reverse_=>1,
		 RC1=>temp_RC1);
		EXCEPTION
			WHEN NO_DATA_FOUND THEN
				NULL;
			WHEN OTHERS THEN
				StoO_error := SQLCODE;
				StoO_errmsg := SQLERRM;
		END;
/* Need to uncomment this later when Mark D has a fix for handling 2 resultsets
	EXEC REV_PRC_GETWIPADJAPPRECDTL @adjustgroupno = @adjustgroupno, @reverse = 1
                 */
	ELSE
	BEGIN
		BEGIN
		REV_PRC_GETWIPADJAPPRECDTL(adjustgroupno=>REV_PRC_GETWIPADJAPPROVERECD.adjustgroupno,
		 reverse_=>0,
		 RC1=>temp_RC1);
		EXCEPTION
			WHEN NO_DATA_FOUND THEN
				NULL;
			WHEN OTHERS THEN
				StoO_error := SQLCODE;
				StoO_errmsg := SQLERRM;
		END;
	END;
	END IF;
END REV_PRC_GETWIPADJAPPROVERECD;
/*
