Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."REV_PRC_SP_GETCOSTVARIATION" (
CHARGE_CODE      VARCHAR2 ,
in_input_type  IN VARCHAR2 ,
RC1 			  IN OUT Rev_Prc_Sp_Getcostvariationpkg.RCT1)
AS
input_type     GLOBALPKG.STRING;
StoO_selcnt		  INTEGER;
StoO_error 		  INTEGER;
StoO_rowcnt		  INTEGER;
StoO_crowcnt	  INTEGER := 0;
StoO_fetchstatus  INTEGER := 0;
StoO_errmsg		  GLOBALPKG.STRING;
StoO_sqlstatus	  INTEGER;
BEGIN
/*
   Called From	 	 :
   Procedure               :-
   Trigger                    :-
   PMD TransactionId  :-
   WMD ActionId         :-
   Modified By             :  sundar
   Modified Date          :  07/25/2001
*/

  Rev_Prc_Sp_Getcostvariation.input_type := Rev_Prc_Sp_Getcostvariation.in_input_type;
  Rev_Prc_Sp_Getcostvariation.input_type :=  RTRIM(Rev_Prc_Sp_Getcostvariation.input_type);
--	   || '%';
	OPEN RC1 FOR
  SELECT PREXTERNALID
  FROM PRTYPECODE
  WHERE PRISOPEN <> 0
   AND UPPER( PREXTERNALID ) LIKE UPPER( Rev_Prc_Sp_Getcostvariation.input_type ) || '%'
	ORDER BY 1 ASC;
END Rev_Prc_Sp_Getcostvariation;
