Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."RPT_SETUP_SP" (
   P_JOB_RUN_ID    IN   NUMBER,
   P_JOB_USER_ID   IN   NUMBER,
   P_POPULATE_UNIV_CALENDAR   IN   NUMBER,
   P_POPULATE_INV_HIERARCHY   IN   NUMBER,
   P_POPULATE_WBS_INDEX       IN   NUMBER,
   P_POPULATE_SKILLS_INDEX    IN   NUMBER,
   P_POPULATE_OBS             IN   NUMBER
)
IS
BEGIN

  IF P_POPULATE_UNIV_CALENDAR = 1 THEN
    RPT_CALENDAR_SP;
  END IF;
  IF P_POPULATE_INV_HIERARCHY = 1 THEN
    RPT_PROGRAM_HIERARCHY_SP;
    RPT_INV_HIERARCHY_SP;
  END IF;
  IF P_POPULATE_WBS_INDEX = 1 THEN
    RPT_WBSINDEX_SP;
  END IF;
  IF P_POPULATE_SKILLS_INDEX = 1 THEN
    RPT_RES_SKILLS_INDEX_SP;
  END IF;
  IF P_POPULATE_OBS = 1 THEN
    NBI_POPULATE_OBS_DIM_SP;
  END IF;

  COMMIT;

EXCEPTION
   WHEN OTHERS
   THEN
      RAISE_APPLICATION_ERROR (
         -20000,
         'Error in RPT_SETUP_SP - ' || SQLERRM
      );
END;
