Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."SP_CHECKCONTRACTAMOUNT" (
  COMPANY_CODE  VARCHAR2 ,
  PROJECT_CODE  VARCHAR2 ,
  billamount  NUMBER ,
  amountexceeded  IN OUT NUMBER)
AS
  StoO_selcnt INTEGER;
  StoO_error  INTEGER;
  StoO_rowcnt INTEGER;
  StoO_crowcnt  INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg GLOBALPKG.STRING;
  StoO_sqlstatus  INTEGER;
  amountbilled  NUMBER(32,6);
  contractamount  NUMBER(32,6);
BEGIN
 /* Called From:
     Procedure         :-
     Trigger           :-
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   : Tarun
   Modified Date : 07/25/2001
*/
	BEGIN
		StoO_rowcnt := 0;
		StoO_selcnt := 0;
		StoO_error  := 0;
		SELECT   amountbilled
    INTO sp_checkcontractamount.amountbilled FROM pac_mnt_projects
		WHERE COMPANY_CODE = sp_checkcontractamount.COMPANY_CODE
		 and PROJECT_CODE = sp_checkcontractamount.PROJECT_CODE;
		StoO_rowcnt := SQL%ROWCOUNT;
		EXCEPTION
			WHEN TOO_MANY_ROWS THEN
				StoO_rowcnt := 2;
			WHEN NO_DATA_FOUND THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
			WHEN OTHERS THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
				StoO_error := SQLCODE;
				StoO_errmsg := SQLERRM;
	END;
	BEGIN
		StoO_rowcnt := 0;
		StoO_selcnt := 0;
		StoO_error  := 0;
		SELECT   contractamount
		INTO sp_checkcontractamount.contractamount FROM projcntrts
		WHERE COMPANY_CODE = sp_checkcontractamount.COMPANY_CODE
		 and PROJECT_CODE = sp_checkcontractamount.PROJECT_CODE;
		StoO_rowcnt := SQL%ROWCOUNT;
		EXCEPTION
			WHEN TOO_MANY_ROWS THEN
				StoO_rowcnt := 2;
			WHEN NO_DATA_FOUND THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
			WHEN OTHERS THEN
				StoO_rowcnt := 0;
				StoO_selcnt := 0;
				StoO_error := SQLCODE;
				StoO_errmsg := SQLERRM;
	END;
	sp_checkcontractamount.amountexceeded :=  NVL(sp_checkcontractamount.contractamount,
	   0) - NVL(sp_checkcontractamount.amountbilled, 0) - sp_checkcontractamount.billamount;
END sp_checkcontractamount;
