Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."SP_DDDW_VENDORCODE" (
arg1 	VARCHAR2  DEFAULT NULL,
RC1 	IN OUT SP_DDDW_VENDORCODEPkg.RCT1)
AS
StoO_selcnt	INTEGER;
StoO_error 	INTEGER;
StoO_rowcnt	INTEGER;
StoO_crowcnt	INTEGER := 0;
StoO_fetchstatus	INTEGER := 0;
StoO_errmsg	GLOBALPKG.STRING;
StoO_sqlstatus	INTEGER;
BEGIN
 /* Called From:
     Procedure         :-
     Trigger           :-
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   : Tarun
   Modified Date : 07/25/2001
*/
	IF  sp_dddw_vendorcode.arg1 IS NULL THEN
		OPEN RC1 FOR
		SELECT VENDOR_CODE,  VendorName FROM int_Vendors;
	ELSE
		OPEN RC1 FOR
		SELECT VENDOR_CODE,  VendorName FROM int_Vendors
		WHERE VENDOR_CODE = sp_dddw_vendorcode.arg1;
	END IF;
END sp_dddw_vendorcode;
