Record.trigger_body:
begin
  DECLARE
	  v_comp_id GLOBALPKG.STRING;
	  v_exist number;
BEGIN
  IF( :NEW.PRIMARY_CONTACT_NAME != :OLD.PRIMARY_CONTACT_NAME ) THEN
    SELECT count(*) INTO v_exist
      FROM ARMASTER A,
          SRM_COMPANIES CM
    WHERE :new.ID = CM.ID
      and CM.COMPANY_ID = A.COMPANY_CODE;

    if v_exist=1 then
      SELECT CM.COMPANY_ID INTO v_comp_id
        from ARMASTER A,
            SRM_COMPANIES CM
      WHERE :new.ID = CM.ID
        and CM.COMPANY_ID = A.COMPANY_CODE;

      UPDATE ARMASTER
          set CONTACT_NAME =:new.PRIMARY_CONTACT_NAME,
              ATTENTION_NAME = :new.PRIMARY_CONTACT_NAME
        where ARMASTER.COMPANY_CODE = v_comp_id;
    END IF;
  END IF;
END;  
end;
