Record.trigger_body:
begin

  if ( :new.odf_object_code = 'project' )
  then
	if ( :new.code != :old.code )
	then
  	clb_dms_modify_folder_pkg.update_folder_name_by_obj_id('Projects',
                                                         :new.id,
										                                     :new.code);
	end if;
  end if;

end;
