Record.trigger_body:
DECLARE
   PRAGMA AUTONOMOUS_TRANSACTION;
begin

	lock table obs_units_flat_by_mode in exclusive mode;
	--
	--	remove all reference to this unit
	--
	delete	obs_units_flat_by_mode
	where		unit_id = :old.id
	or			linked_unit_id = :old.id;

	commit;

exception
   when others
   then
      rollback;

end;
