Record.trigger_body:
begin
 declare
 TYPE cur_typ IS REF CURSOR;
 c cur_typ;

 begin

  update cmn_audits
  set    object_id   = :new.primary_object_instance_id,
         object_code = :new.primary_object_instance_code
  where  object_id   = :new.secondary_object_instance_id
  and    object_code = :new.secondary_object_instance_code;

  update cmn_audits_tsv
  set    object_id   = :new.primary_object_instance_id,
         object_code = :new.primary_object_instance_code
  where  object_id   = :new.secondary_object_instance_id
  and    object_code = :new.secondary_object_instance_code;

 end;
end;
