Record.trigger_body:
DECLARE
   PRAGMA AUTONOMOUS_TRANSACTION;
begin

	lock table ODF_OBJECT_MAPPING_FLAT in exclusive mode;
	--
	--	remove all reference to this 
	--
      delete
      from odf_object_mapping_flat
      where obj_mapping_code = :old.mapping_code
      or ancestor_obj_mapping_code = :old.mapping_code;
	commit;

exception
   when others
   then
      rollback;

end;
