Record.trigger_body:
DECLARE
  StoO_selcnt       INTEGER;
  StoO_error        INTEGER;
  StoO_rowcnt       INTEGER := 1;
  StoO_crowcnt      INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg       GLOBALPKG.STRING;
  StoO_sqlstatus    INTEGER;
  lCount            NUMBER;
BEGIN
  -- are there any PROJECT_CODE site columns in the matrix
  IF (:NEW.MatrixType != :OLD.MatrixType) THEN
    BEGIN
      BEGIN
        StoO_rowcnt := 0;
        StoO_selcnt := 0;
        StoO_error  := 0;

        SELECT COUNT(*) INTO lCount FROM DUAL
         WHERE :OLD.MatrixType <> 'R'
           AND :NEW.MatrixType = 'R'
           AND EXISTS (SELECT 1
                         FROM ppa_MatrixColDef
                        WHERE FieldName = 'projsitecode'
                          AND MatrixKey = :OLD.MatrixKey);
        StoO_rowcnt := SQL%ROWCOUNT;

        EXCEPTION
          WHEN TOO_MANY_ROWS THEN
            StoO_rowcnt := 2;
          WHEN NO_DATA_FOUND THEN
            StoO_rowcnt := 0;
            StoO_selcnt := 0;
          WHEN OTHERS THEN
            StoO_rowcnt := 0;
            StoO_selcnt := 0;
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
      END;

      IF lCount <> 0 THEN
        BEGIN
          raise_application_error(-20432, 'You cannot change matrix type to rate when used by a PROJECT_CODE.');
        END;
      END IF;
    END;
	END IF;
END ppa_MatrixUpdateTrigger;
