Record.trigger_body:
BEGIN
    UPDATE PAC_MNT_PROJECTS SET TARGETEDSTARTDATE = :NEW.SCHEDULE_START,
    TARGETEDCOMPLDATE = :NEW.SCHEDULE_FINISH,
    STARTDATE = :NEW.SCHEDULE_START,
    COMPLETEDATE = :NEW.SCHEDULE_FINISH
    WHERE PAC_MNT_PROJECTS.ID = :OLD.ID;
EXCEPTION
    WHEN NO_DATA_FOUND THEN NULL;
END PRJ_PROJECTS_T1;
