Record.trigger_body:
BEGIN
	IF
	(
    ( ( :NEW.BASELINE_ID IS NULL ) AND ( :OLD.BASELINE_ID IS NOT NULL ) )
		OR
    ( ( :NEW.BASELINE_ID IS NOT NULL ) AND ( :OLD.BASELINE_ID IS NULL ) )
		OR
    ( :NEW.BASELINE_ID <> :OLD.BASELINE_ID )

	)
	THEN
	  UPDATE PRJ_BASELINES SET SLICE_STATUS = 1 WHERE ID=:OLD.BASELINE_ID OR ID=:NEW.BASELINE_ID;
 	END IF;
END;
