Record.trigger_body:
BEGIN
	IF 
	(
		INSERTING
    )
    THEN
    		:NEW.SLICE_STATUS := 1;
            :NEW.HARD_SLICE_STATUS := 1;
    END IF;

    IF
    (

		UPDATING( 'PRALLOCCURVE' )

		OR

		( ( :NEW.PRAVAILSTART IS NULL ) AND ( :OLD.PRAVAILSTART IS NOT NULL ) )
		OR
		( ( :NEW.PRAVAILSTART IS NOT NULL ) AND ( :OLD.PRAVAILSTART IS NULL ) ) 
		OR
		( :NEW.PRAVAILSTART <> :OLD.PRAVAILSTART )

		OR

		( ( :NEW.PRAVAILFINISH IS NULL ) AND ( :OLD.PRAVAILFINISH IS NOT NULL ) ) 
		OR
		( ( :NEW.PRAVAILFINISH IS NOT NULL ) AND ( :OLD.PRAVAILFINISH IS NULL ) ) 
		OR
		( :NEW.PRAVAILFINISH <> :OLD.PRAVAILFINISH )
	)
	THEN	
    		:NEW.SLICE_STATUS := 1;
  	END IF;

  IF
  (
    UPDATING( 'HARD_CURVE' )
  )
  THEN
    :NEW.HARD_SLICE_STATUS := 1;
  END IF;

  IF
  (
    UPDATING( 'INCIDENT_ACT_CURVE' )
  )
  THEN
    :NEW.INCIDENT_SLICE_STATUS := 1;
  END IF;


END;
