Record.trigger_body:
BEGIN IF ( ( ( :NEW.DATE_OF_HIRE IS NULL ) AND ( :OLD.DATE_OF_HIRE IS NOT NULL ) ) OR ( ( :NEW.DATE_OF_HIRE IS NOT NULL ) AND ( :OLD.DATE_OF_HIRE IS NULL ) ) OR ( :NEW.DATE_OF_HIRE <> :OLD.DATE_OF_HIRE )
OR
( ( :NEW.DATE_OF_TERMINATION IS NULL ) AND ( :OLD.DATE_OF_TERMINATION IS NOT NULL ) ) OR ( ( :NEW.DATE_OF_TERMINATION IS NOT NULL ) AND ( :OLD.DATE_OF_TERMINATION IS NULL ) ) OR ( :NEW.DATE_OF_TERMINATION <> :OLD.DATE_OF_TERMINATION ) ) THEN :NEW.SLICE_STATUS := 1; END IF; END;
