Record.trigger_body:
begin


 	  DECLARE

	  v_exist number;
    begin

        IF ( :NEW.COMPANY_NAME != :OLD.COMPANY_NAME )  THEN
        	  SELECT count(*) INTO v_exist
        	  FROM CLNTSUPP A

          	  WHERE :new.ID = A.SRM_ID ;

        	  if v_exist=1 then  BEGIN



        		 UPDATE ARMASTER
        		   set
        		   ARMASTER.ADDRESS_NAME =:new.COMPANY_NAME
        		   where ARMASTER.SRM_ID = :new.ID;

        		   UPDATE CLNTSUPP
        		   set
        		   CLNTSUPP.COMPANY_NAME =:new.COMPANY_NAME
        		   where CLNTSUPP.SRM_ID =:new.ID;
        		   END;
        	  END IF;
        END IF;
    end;

end;
