Record.trigger_body:
DECLARE
  StoO_selcnt       INTEGER;
  StoO_error        INTEGER;
  StoO_rowcnt       INTEGER := 1;
  StoO_crowcnt      INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg       GLOBALPKG.STRING;
  StoO_sqlstatus    INTEGER;
BEGIN
	BEGIN
    StoO_selcnt := 0;
    StoO_error  := 0;
    StoO_rowcnt := 0;

    SELECT 1 INTO StoO_selcnt
    FROM DUAL
    WHERE  EXISTS (
      SELECT  1
      FROM clntsupp
      WHERE :OLD.batchcycle = clntsupp.billcycle);

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        StoO_rowcnt := 0;
        StoO_selcnt := 0;
      WHEN OTHERS THEN
        StoO_rowcnt := 0;
        StoO_selcnt := 0;
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;

  IF StoO_selcnt != 0 THEN
    BEGIN
      raise_application_error(-20449, 'Cannot delete this batchcycle because it has been assigned to a COMPANY_CODE in the clntsupp table.');
      RETURN;
    END;
	END IF;

	BEGIN
    BEGIN
      StoO_selcnt := 0;
      StoO_error  := 0;
      StoO_rowcnt := 0;

      SELECT 1 INTO StoO_selcnt
      FROM DUAL
      WHERE  EXISTS (
        SELECT  1
        FROM pac_mnt_projects
        WHERE billcycle = :OLD.batchcycle);

      StoO_rowcnt := SQL%ROWCOUNT;
      EXCEPTION
        WHEN NO_DATA_FOUND THEN
          StoO_rowcnt := 0;
          StoO_selcnt := 0;
        WHEN OTHERS THEN
          StoO_rowcnt := 0;
          StoO_selcnt := 0;
          StoO_error := SQLCODE;
          StoO_errmsg := SQLERRM;
    END;

    IF StoO_selcnt != 0 THEN
      BEGIN
        raise_application_error(-20450, 'Cannot delete this batchcycle because it has been assigned to a PROJECT_CODE in the projects table.');
        RETURN;
      END;
    END IF;
	END;

  BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;

    DELETE  batchbillaccess b
      WHERE b.batchcycle = :OLD.batchcycle;

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
	END;
END tr_batchsettings_delete;
