Record.trigger_body:
DECLARE
  StoO_selcnt       INTEGER;
  StoO_error        INTEGER;
  StoO_rowcnt       INTEGER := 1;
  StoO_crowcnt      INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg       GLOBALPKG.STRING;
  StoO_sqlstatus    INTEGER;
BEGIN
	BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;

    DELETE projstaff
     WHERE projstaff.COMPANY_CODE = :OLD.COMPANY_CODE
       AND projstaff.RESOURCE_CODE = :OLD.RESOURCE_CODE;

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
      WHEN OTHERS THEN
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
    END;

    IF StoO_error != 0 THEN
    BEGIN
      raise_application_error(-20458, 'Unable to delete records in projstaff.');
      RETURN;
    END;
	END IF;
END tr_clntresponse_delete;
