Record.trigger_body:
DECLARE
  StoO_selcnt       INTEGER;
  StoO_error        INTEGER;
  StoO_rowcnt       INTEGER := 1;
  StoO_crowcnt      INTEGER := 0;
  StoO_fetchstatus  INTEGER := 0;
  StoO_errmsg       GLOBALPKG.STRING;
  StoO_sqlstatus    INTEGER;
BEGIN
	BEGIN
    StoO_selcnt := 0;
    StoO_error  := 0;
    StoO_rowcnt := 0;

    SELECT 1 INTO StoO_selcnt
    FROM DUAL
    WHERE  EXISTS (
      SELECT  1
      FROM olb_BillingCheckout
      WHERE :NEW.COMPANY_CODE = olb_BillingCheckout.COMPANY_CODE
      AND :NEW.PROJECT_CODE = olb_BillingCheckout.PROJECT_CODE );

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        StoO_rowcnt := 0;
        StoO_selcnt := 0;
      WHEN OTHERS THEN
        StoO_rowcnt := 0;
        StoO_selcnt := 0;
        StoO_error := SQLCODE;
        StoO_errmsg := SQLERRM;
  END;

  IF StoO_selcnt != 0 THEN
    BEGIN
      raise_application_error(-20328, 'Originating transaction is being billed');
    END;
	END IF;
END tr_ppa_TransWIPAdjust_insert;
