                  SELECT @SELECT:p.id:pid@,
       @SELECT:nls.name:pname@,
       @SELECT:nls.description:description@, @SELECT:p.start_date:stdate@,
       @SELECT:p.end_date - 1:edate@,
       @SELECT:e.entity:entity@,
       @SELECT:p.last_updated_date:last_updated_date@,
       @SELECT:lang.language_code:language_code@,
       @SELECT:lang.id:language_id@
FROM   biz_com_periods p
       INNER JOIN cmn_captions_nls nls ON  p.id = nls.pk_id
                                       AND nls.table_name = 'BIZ_COM_PERIODS'
       INNER JOIN cmn_languages lang ON nls.language_code =lang.language_code
       INNER JOIN entity e ON  p.entity_id = e.id
                           AND e.entity = (SELECT defaultentity FROM nameoptions)
WHERE  p.period_type = 'QUARTERLY'
AND    nls.language_code = @WHERE:PARAM:LANGUAGE@
@BROWSE-ONLY:
AND    p.is_active = 1
:BROWSE-ONLY@
AND    @FILTER@

