dynamicLookup.nsql:
SELECT @SELECT:P.ID:PID@, 
             @SELECT:NLS.NAME:PNAME@,
             @SELECT:NLS.DESCRIPTION:DESCRIPTION@,
             @SELECT:L.NAME:LNAME@,
             @SELECT:P.START_DATE:STDATE@, 
             @SELECT:P.END_DATE-1:EDATE@,
             @SELECT:E.ENTITY:ENTITY@,
             @SELECT:LANG.ID:LANGUAGE_ID@,
             @SELECT:LANG.LANGUAGE_CODE:LANGUAGE_CODE@,
             @SELECT:P.LAST_UPDATED_DATE:LAST_UPDATED_DATE@
       FROM BIZ_COM_PERIODS P, CMN_LOOKUPS_V L, CMN_CAPTIONS_NLS NLS, CMN_LANGUAGES LANG, ENTITY E
      WHERE P.ID = NLS.PK_ID
        AND NLS.TABLE_NAME='BIZ_COM_PERIODS'
		AND L.LANGUAGE_CODE = LANG.LANGUAGE_CODE
        AND NLS.LANGUAGE_CODE = @WHERE:PARAM:LANGUAGE@
        AND L.LANGUAGE_CODE = @WHERE:PARAM:LANGUAGE@
        AND L.LOOKUP_TYPE = 'PERIOD_TYPE'
        AND L.LOOKUP_CODE = P.PERIOD_TYPE
        @BROWSE-ONLY:
        AND P.IS_ACTIVE = 1
        AND P.ENTITY_ID=E.ID
        :BROWSE-ONLY@
        AND (@WHERE:PARAM:USER_DEF:STRING:ENTITY_ID@ IS NULL OR
             @WHERE:PARAM:USER_DEF:STRING:ENTITY_ID@ = E.ENTITY)
        AND (@WHERE:PARAM:USER_DEF:STRING:PERIOD_TYPE@ IS NULL OR
             @WHERE:PARAM:USER_DEF:STRING:PERIOD_TYPE@ = P.PERIOD_TYPE)                  
        AND @FILTER@

