                @SELECT:RESOURCES.FIRST_NAME:FIRST_NAME@,
                @SELECT:RESOURCES.FULL_NAME:FULL_NAME@,
                @SELECT:RESOURCES.UNIQUE_NAME:UNIQUE_NAME@,
                @SELECT:RESOURCES.UNIQUE_NAME:UNIQUE_CODE@,
        @SELECT:(case
		           when RESOURCES.RESOURCE_TYPE=0 then 'L'
		           when RESOURCES.RESOURCE_TYPE=1 then 'Q'
		           when RESOURCES.RESOURCE_TYPE=2 then 'M'
		           when RESOURCES.RESOURCE_TYPE=3 then 'X'
        END):resource_type@,
        @SELECT:NLS.name:resource_type_caption@,
        @SELECT:LANG.ID:LANGUAGE_ID@,
        @SELECT:LANG.LANGUAGE_CODE:LANGUAGE_CODE@,
        @SELECT:RESOURCES.LAST_UPDATED_DATE:LAST_UPDATED_DATE@

                FROM SRM_RESOURCES RESOURCES, PRJ_RESOURCES PR, CMN_LOOKUPS_V NLS, CMN_LANGUAGES LANG
                WHERE
                @WHERE:SECURITY:RESOURCE:RESOURCES.ID@               AND
                PR.PRISROLE = 0                                      AND
