query.nsql:
SELECT DISTINCT @SELECT:DIM:USER_DEF:IMPLIED:PARTITIONS:X.MODEL_CODE:CODE@,
		@SELECT:DIM_PROP:USER_DEF:IMPLIED:PARTITIONS:X.LAST_UPDATED_DATE:LAST_UPDATED_DATE@,
		@SELECT:DIM_PROP:USER_DEF:IMPLIED:PARTITIONS:CSU.USER_NAME:LAST_UPDATED_BY@

FROM
( select cp.MODEL_CODE, last_updated_by, last_updated_date from cmn_partitions cp
	union all
	select code as model_code, last_updated_by, last_updated_date from CMN_PARTITION_MODELS cpm
) x
LEFT JOIN cmn_sec_users csu ON csu.id = x.last_updated_by

where EXISTS (
select last_updated_date, MODEL_CODE from (
	select max(xmax.LAST_UPDATED_DATE) last_updated_date , xmax.MODEL_CODE
	from
		(
			select cp.MODEL_CODE, last_updated_date from cmn_partitions cp
			union all
			select code as model_code, last_updated_date from CMN_PARTITION_MODELS cpm
		) xmax group by xmax.model_code
	) xmaxx where xmaxx.last_updated_date = x.last_updated_date and x.model_code = xmaxx.model_code
)
and
@FILTER@
