query.nsql:
SELECT @SELECT:DIM:USER_DEF:IMPLIED:INDEX:UI.TABLE_NAME:CODE@,
  @SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.TABLE_NAME:TABLE_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.INDEX_NAME:INDEX_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.INDEX_TYPE:INDEX_TYPE@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.UNIQUENESS:UNIQUENESS@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.TABLESPACE_NAME:TABLESPACE_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.STATUS:STATUS@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.DEGREE:DEGREE@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.LOGGING:LOGGING@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX:UI.DROPPED:DROPPED@
FROM user_indexes ui
WHERE ui.index_type != 'LOB'
	AND @FILTER@
