query.nsql:
SELECT @SELECT:DIM:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.TABLE_NAME:CODE@,
  @SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.TABLE_NAME:TABLE_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.INDEX_NAME:INDEX_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.COLUMN_NAME:COLUMN_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.COLUMN_POSITION:COLUMN_POSITION@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIC.DESCEND:DESCEND@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:INDEX_COLUMN:UIE.COLUMN_EXPRESSION:COLUMN_EXPRESSION@
FROM user_ind_columns uic
LEFT JOIN user_ind_expressions uie ON uie.table_name = uic.table_name
																	AND uie.index_name = uic.index_name
																	AND uie.column_position = uic.column_position
WHERE @FILTER@
