query.nsql:
SELECT @SELECT:DIM:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.TABLE_NAME:CODE@,
  @SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.TABLE_NAME:TABLE_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.CONSTRAINT_NAME:CONSTRAINT_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.CONSTRAINT_TYPE:CONSTRAINT_TYPE@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.SEARCH_CONDITION:SEARCH_CONDITION@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.DELETE_RULE:DELETE_RULE@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.STATUS:STATUS@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.DEFERRABLE:DEFERRABLE@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.DEFERRED:DEFERRED@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.VALIDATED:VALIDATED@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.INDEX_NAME:INDEX_NAME@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.INVALID:INVALID@,
	@SELECT:DIM_PROP:USER_DEF:IMPLIED:TABLE_CONSTRAINT:UC.VIEW_RELATED:VIEW_RELATED@
FROM user_constraints uc
WHERE uc.constraint_type = 'P'
	AND @FILTER@
