query.nsql:
SELECT   @SELECT:DIM:USER_DEF:IMPLIED:ACTIVE_JOB:@DBUSER@.CMN_TO_CHAR_FCT(ACTIVE_JOB_RUNS.JOB_DEFINITION_ID):JOB_DEFINITION_ID@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:@DBUSER@.CMN_TO_CHAR_FCT(ACTIVE_JOB_RUNS.SCH_JOB_ID):SCH_JOB_ID@,
 @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:ACTIVE_JOB_RUNS.SCH_JOB_NAME:SCH_JOB_NAME@,
 @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:ACTIVE_JOB_RUNS.SCH_JOB_DEF_NAME:SCH_JOB_DEF_NAME@,
 @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:@DBUSER@.CMN_TO_CHAR_FCT(ACTIVE_JOB_RUNS.JOB_RUN_ID):JOB_RUN_ID@,
 @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:ACTIVE_JOB_RUNS.SCHEDULER_ID:SCHEDULER_ID@,
 @SELECT:DIM_PROP:USER_DEF:IMPLIED:ACTIVE_JOB:ACTIVE_JOB_RUNS.JOB_DESCRIPTION:JOB_DESCRIPTION@
FROM
 CMN_SCH_JOB_CURR_RUNS ACTIVE_JOB_RUNS
WHERE ACTIVE_JOB_RUNS.JOB_DEFINITION_ID != -1 AND @FILTER@
