query.nsql:
SELECT   @SELECT:DIM:USER_DEF:IMPLIED:log:z.id:id@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.host_name:host_name@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.start_time:start_time@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.finish_time:finish_time@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.action_method:action_method@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.url_short:url_short@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.bytes_sent:bytes_sent@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:round(z.elapsed_time/1000,1):elapsed_time@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:z.session_cookie:session_cookie@,
         @SELECT:DIM_PROP:USER_DEF:IMPLIED:log:u.user_name:user_name@
FROM     LOG_DETAILS Z
LEFT OUTER JOIN LOG_SESSIONS S ON (Z.SESSION_COOKIE = S.TOKEN)
LEFT OUTER JOIN CMN_SEC_USERS U ON (S.USER_ID = U.ID)
WHERE  z.LOG_DATE = @WHERE:PARAM:USER_DEF:DATE:logdate@
AND    (@WHERE:PARAM:USER_DEF:STRING:env@ IS NULL OR @WHERE:PARAM:USER_DEF:STRING:env@=Z.ENVIRONMENT)
AND      @FILTER@
