query.nsql:
select @SELECT:DIM:USER_DEF:IMPLIED:LOG:log_date:log_date@,
@SELECT:METRIC:USER_DEF:IMPLIED:sum(view_count):VIEW_TOTAL@,
@SELECT:METRIC:USER_DEF:IMPLIED:CASE sum(VIEW_COUNT) WHEN 0 THEN 0 ELSE round((avg(avg_resp)*sum(view_count)/sum(view_count)/1000),2) END:AVG_RESP@,
@SELECT:METRIC:USER_DEF:IMPLIED:CASE sum(SOAP_VIEW_COUNT) WHEN 0 THEN 0 ELSE round((avg(avg_soap_resp)*sum(soap_view_count)/sum(soap_view_count)/1000),2) END:AVG_SOAP_RESP@,
@SELECT:METRIC:USER_DEF:IMPLIED:SUM(SOAP_VIEW_COUNT):XOG_TOTAL@,
@SELECT:METRIC:USER_DEF:IMPLIED:SUM(SESSION_COUNT):UNIQ_SESSION_COUNT@
FROM LOG_SUMMARY
WHERE ENVIRONMENT = @WHERE:PARAM:USER_DEF:STRING:env@
AND LOG_DATE BETWEEN CAST(@WHERE:PARAM:USER_DEF:DATE:start_date@ AS DATE) AND
CAST(@WHERE:PARAM:USER_DEF:DATE:end_date@ AS DATE)
AND @FILTER@
GROUP BY LOG_DATE
